// break out the input into (room, device, attribute, value)
// output nodes:
//  0 - office, lab, apower
//  1 - office, lab, voltage
//  2 - office, lab, current
//  3 - office, lab, temperature
//  4 - office, desk, apower
//  5 - office, desk, voltage
//  6 - office, desk, current
//  7 - office, desk, temperature
//  8 - garage, EV, apower
//  9 - garage, EV, voltage
// 10 - garage, EV, current
// 11 - garage, EV, temperature

// Split up the topic and ensure we have a valid topic string
const topicParts = msg.topic.split('/');
if (topicParts.length < 3) {
    node.warn("Invalid topic format: " + msg.topic);
    return null;
}

// topic string is expected to be in the format of
//    [0]           [1]         [2]      [3]      [4]     [5]           [6]
//   <root>/<publishing host>/<room>/smartplugs/<area>/switch:0/<attribute name>
// and of course the payload is the value for the attribute

const room = topicParts[2]
const area = topicParts[4]
const attributeName = topicParts[topicParts.length - 1];
const attributeValue = String(msg.payload); // Convert payload to string

// Helper function to create message array
function createMessageArray(position, value) {
    const arr = Array(12).fill(null);
    if (value !== null) {
        arr[position] = { payload: value };
    }
    return arr;
}

if (room === "office" && area === "Shelly_Lab_01") {
    if (attributeName === "apower") {
        return createMessageArray(0, attributeValue);
    } else if (attributeName === "voltage") {
        return createMessageArray(1, attributeValue);
    } else if (attributeName === "current") {
        return createMessageArray(2, attributeValue);
    } else if (attributeName === "tF") {
        return createMessageArray(3, attributeValue);
    }
} else if (room === "office" && area === "Shelly_Prod") {
    if (attributeName === "apower") {
        return createMessageArray(4, attributeValue);
    } else if (attributeName === "voltage") {
        return createMessageArray(5, attributeValue);
    } else if (attributeName === "current") {
        return createMessageArray(6, attributeValue);
    } else if (attributeName === "tF") {
        return createMessageArray(7, attributeValue);
    }
} else if (room === "garage" && area === "Shelly_EV") {
    if (attributeName === "apower") {
        return createMessageArray(8, attributeValue);
    } else if (attributeName === "voltage") {
        return createMessageArray(9, attributeValue);
    } else if (attributeName === "current") {
        return createMessageArray(10, attributeValue);
    } else if (attributeName === "tF") {
        return createMessageArray(11, attributeValue);
    }
} else {
    node.warn("Invalid room/area: " + room + "/" + area);
    return null;
}

// Function to process MQTT messages and route based on device name
function formatTimestamp(timestamp) {
    console.log("Received timestamp:", timestamp, "Type:", typeof timestamp);

    // Ensure we have a valid Date object
    const date = new Date(Number(timestamp));

    if (isNaN(date.getTime())) {
        console.log("Error: Invalid date created");
        return new Date().toLocaleString('en-US', {
            timeZone: 'America/Los_Angeles',
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
        }).replace(/(\d+)\/(\d+)\/(\d+),\s+(\d+:\d+).*/, '$3-$1-$2 $4');
    }

    return date.toLocaleString('en-US', {
        timeZone: 'America/Los_Angeles',
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
    }).replace(/(\d+)\/(\d+)\/(\d+),\s+(\d+:\d+).*/, '$3-$1-$2  $4');
}

// Get the formatted timestamp from the message
const new_payload = formatTimestamp(msg.timestamp);

// Split the topic into parts and get the device name (5th part)
const parts = msg.topic.split('/');
const device_name = parts[4] || '';

let output = [null, null, null];

// Switch based on device_name
msg.payload = new_payload;
switch(device_name) {
    case 'Shelly_Lab_01':
        output[0] = msg;
        break;
    case 'Shelly_Prod':
        output[1] = msg;
        break;
    case 'Shelly_EV':
        output[2] = msg;
        break;
    // Default case already has all nulls
}

// Return the array of messages
return output;
